import R from 'ramda'

import {
    ADD_ITEM_TO_LIST,
    REMOVE_ITEM_FROM_LIST,
    CLEAN_LIST
} from 'actionTypes'

const initialState = []

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case ADD_ITEM_TO_LIST:
            return R.append(payload, state)
        case REMOVE_ITEM_FROM_LIST:
            return R.without(R.of(payload), state)
        case CLEAN_LIST:
            return []
        default: 
            return state
    }
}
